passages = passages[:70]
questions = questions[:70]

# Dictionary to store results
results_dict = {}

results_dict = {}

for question in questions:
    max_score = float("-inf")
    passage_with_max_score = None

    # Encode Question
    encoded_question = question_encoder_tokenizer(question, return_tensors="pt", padding=True, truncation=True)
    question_embeddings = question_encoder(**encoded_question).pooler_output

    for passage in passages:
        # Encode passage
        encoded_passage = passage_encoder_tokenizer(passage, return_tensors="pt", padding=True, truncation=True)
        passage_embeddings = passage_encoder(**encoded_passage).pooler_output

        # Calculate cosine similarity scores
        similarity_scores = cos_sim(question_embeddings.repeat(len(passage_embeddings), 1), passage_embeddings)

        # Get passage with highest similarity score for this question
        max_score_for_passage, _ = torch.max(similarity_scores, dim=0)

        # Check if this score is the highest for this question
        if max_score_for_passage > max_score:
            max_score = max_score_for_passage
            passage_with_max_score = passage

    # Store results in dictionary
    results_dict[question] = [max_score.item(), 1, passage_with_max_score]

# Print results dictionary
print(results_dict)

